    #make ARCH=arm64 O=out/$NAMES savedefconfig
    #cp out/$NAMES/defconfig arch/arm64/configs/vendor/$(echo $DEVICE)_defconfig
    make -j$(nproc --all) O=out/$NAMES ARCH=arm64 CC=clang CLANG_TRIPLE=aarch64-linux-gnu- CROSS_COMPILE=aarch64-linux-android- LLVM=1 LLVM_IAS=1
}

# Main script logic
while true; do
    display_menu
    read -p "Enter your choice: " choice
    set_environment $choice
    if [[ $choice -ge 1 && $choice -le 6 ]]; then
        next_command
        break
    fi
done
